import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faAngleRight, faAngleLeft, faPause } from "@fortawesome/free-solid-svg-icons";
const Player = ({ currentSong, isplaying, setIsPlaying, audioRef, songs }) => {

    //selecting HTML via useRef

    const playSongHandler = () => {
        if (isplaying) {
            setIsPlaying(!isplaying);
            audioRef.current.pause()
        } else {
            setIsPlaying(!isplaying)
            audioRef.current.play()
        }
    }
    //timeUpateHandler
    const timeUpateHandler = (e) => {
        const current = e.target.currentTime;
        const duration = e.target.duration;
        setSongInfo({ ...songinfo, currentTime: current, duration: duration })

    }


    const getTime = (time) => {
        return (
            Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
        )
    }
    const [songinfo, setSongInfo] = useState({
        currentTime: 0,
        duration: 0
    })

    const dragHandler = (e) => {
        audioRef.current.currentTime = e.target.value;
        setSongInfo({ ...songinfo, currentTime: e.target.value })
    }
    const skipTrackHandler = (direction) => {
        const currentIndex = songs.findIndex((song) => { song.id === currentSong.id })
    }
    return (
        <div className="player">
            <div className="time-control">
                <p>{getTime(songinfo.currentTime)}</p>
                <input type="range" min={0} max={songinfo.duration} value={songinfo.currentTime} onChange={dragHandler} />
                <p>{getTime(songinfo.duration)}</p>

            </div>
            <div className="play-control">
                <FontAwesomeIcon className="skip-back" icon={faAngleLeft} size="2x" />
                <FontAwesomeIcon icon={isplaying ? faPause : faPlay} size="2x" onClick={playSongHandler} />
                <FontAwesomeIcon className="skip-forward" icon={faAngleRight} size="2x" onClick={() => { skipTrackHandler('skip-forward') }} />
            </div>
            <audio src={currentSong.audio} ref={audioRef} onTimeUpdate={timeUpateHandler} onLoadedMetadata={timeUpateHandler}></audio>
        </div>
    )
}

export default Player;
