import React, { useRef } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faAngleRight, faAngleLeft } from "@fortawesome/free-solid-svg-icons";
const player = ({ currentSong }) => {

    //selecting HTML via useRef
    const audioRef = useRef(null);
    const playSongHandler = () => {
        console.log(audioRef);
    }

    return (
        <div className="player">
            <div className="time-control">
                <p>start time</p>
                <input type="range" />
                <p>end time</p>

            </div>
            <div className="play-control">
                <FontAwesomeIcon className="skip-back" icon={faAngleLeft} size="2x" />
                <FontAwesomeIcon icon={faPlay} size="2x" onClick={playSongHandler} />
                <FontAwesomeIcon className="skip-forward" icon={faAngleRight} size="2x" />
            </div>
            <audio src={currentSong.audio} ref={audioRef}></audio>
        </div>
    )
}

export default player;
