import React from "react";

const LibrarySongs = ({ songs, setCurrentsong, song, id, audioRef, isplaying }) => {

    const selectsongHandler = () => {
        const selectedSong = songs.filter(theSong => theSong.id === id);
        setCurrentsong(selectedSong[0])
        const selectedAudio = song.map()

        if (isplaying) {
            const playAudio = audioRef.current.play()
            if (playAudio !== undefined) {
                playAudio.then(() => {
                    audioRef.current.play()
                }).catch((err) => {
                    console.log(err)
                })
            }
        }





    }
    return (
        <div className={song.active ? "library-song" : "selected-song"} onClick={selectsongHandler}>
            <img src={song.cover} alt={song.name} />
            <div className="song-description">
                <h2>{song.name}</h2>
                <h3>{song.artist}</h3>
            </div>

        </div>
    )
}

export default LibrarySongs;
