export const Audio_player = ({ isplaying, audioRef, setIsPlaying }) => {
    if (isplaying) {
        setIsPlaying(false)
        const playAudio = audioRef.current.play()
        console.log(isplaying)
        if (playAudio !== undefined) {
            playAudio.then(() => {
                audioRef.current.play()
            }).catch((err) => {
                console.log(err)
            })
        }
    }
}
