import React, { useRef, useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faAngleRight, faAngleLeft } from "@fortawesome/free-solid-svg-icons";
const Player = ({ currentSong, isplaying, setIsPlaying }) => {

    //selecting HTML via useRef
    const audioRef = useRef(null);
    const playSongHandler = () => {
        if (isplaying) {
            setIsPlaying(!isplaying);
            audioRef.current.pause()
        } else {
            setIsPlaying(!isplaying)
            audioRef.current.play()
        }
    }
    //timeUpateHandler
    const timeUpateHandler = (e) => {
        const current = e.target.currentTime;
        const duration = e.target.duration;
        setSongInfo({ ...songinfo, currentTime: current, duration: duration })

    }


    const getTime = (time) => {
        return (
            Math.floor(time)
        )
    }
    const [songinfo, setSongInfo] = useState({
        currentTime: null,
        duration: null
    })
    return (
        <div className="player">
            <div className="time-control">
                <p>{getTime(songinfo.currentTime)}</p>
                <input type="range" />
                <p>{getTime(songinfo.duration)}</p>

            </div>
            <div className="play-control">
                <FontAwesomeIcon className="skip-back" icon={faAngleLeft} size="2x" />
                <FontAwesomeIcon icon={faPlay} size="2x" onClick={playSongHandler} />
                <FontAwesomeIcon className="skip-forward" icon={faAngleRight} size="2x" />
            </div>
            <audio src={currentSong.audio} ref={audioRef} onTimeUpdate={timeUpateHandler}></audio>
        </div>
    )
}

export default Player;
