export const Audio_player = async ({ isplaying, audioRef }) => {
    if (isplaying) {
        const playAudio = audioRef.current.play()
        if (playAudio !== undefined) {
            playAudio.then(() => {
                audioRef.current.load()
            }).catch((err) => {
                console.log(err)
            })
        }
    }
}
